import {
    ADD_TODO,
    DELETE_TODO,
    EDIT_TODO
} from "./constants";

export const addTodo = (todo) => {
    return {
        type: ADD_TODO,
        payload: todo
    }
}

export const deleteTodo = (id) => {
    return{
        type: DELETE_TODO,
        payload: id
    }
}

export const addEdit = (todo) => {
    return {
        type: ED,
        payload: todo
    }
}
